sourcesContent:
"use client"

import { Button } from "@/components/ui/button"
import { useState, useEffect } from "react"
import { useRouter } from "next/navigation"
import {
  ArrowLeft,
  MoreHorizontal,
  Star,
  MapPin,
  Clock,
  DollarSign,
  Heart,
  Activity,
  Stethoscope,
  Plus,
  User,
  Calendar,
  Award,
} from "lucide-react"

export default function BookingPage({ doctorId }) {
  const router = useRouter()
  const [selectedDate, setSelectedDate] = useState(null)
  const [selectedTime, setSelectedTime] = useState(null)
  const [doctor, setDoctor] = useState({
    id: "1",
    name: "Dr. Arun Kumar",
    specialist: "Cardiologist",
    system: "Cardiovascular",
    hospital: "Apollo Hospitals",
    experience: "15 years",
    availability: "moderate",
    busyRate: "Medium",
    consultationFee: 750,
    rating: 4.8,
    qualifications: "MBBS, MD (Cardiology), DM",
    languages: "English, Hindi, Tamil",
  })

  useEffect(() => {
    // Scroll to top when component mounts
    window.scrollTo(0, 0)
  }, [])

  // Combined doctor data from all sources
  const allDoctors = [
    // Hospital specialists
    {
      id: "1",
      name: "Dr. Arun Kumar",
      specialist: "Cardiologist",
      system: "Cardiovascular",
      hospital: "Apollo Hospitals",
      experience: "15 years",
      availability: "moderate",
      busyRate: "Medium",
      consultationFee: 750,
      rating: 4.8,
      qualifications: "MBBS, MD (Cardiology), DM",
      languages: "English, Hindi, Tamil",
    },
    {
      id: "2",
      name: "Dr. Priya Sharma",
      specialist: "Neurologist",
      system: "Nervous System",
      hospital: "Apollo Hospitals",
      experience: "12 years",
      availability: "available",
      busyRate: "Low",
      consultationFee: 650,
      rating: 4.6,
      qualifications: "MBBS, MD (Neurology)",
      languages: "English, Hindi",
    },
    {
      id: "3",
      name: "Dr. Rajesh Patel",
      specialist: "Orthopedic Surgeon",
      system: "Musculoskeletal",
      hospital: "Apollo Hospitals",
      experience: "18 years",
      availability: "busy",
      busyRate: "High",
      consultationFee: 900,
      rating: 4.9,
      qualifications: "MBBS, MS (Orthopedics)",
      languages: "English, Hindi, Gujarati",
    },
    {
      id: "4",
      name: "Dr. Rajesh Kumar",
      specialist: "General Physician",
      system: "Primary Care",
      hospital: "Apollo Hospitals",
      experience: "12 years",
      availability: "available",
      busyRate: "Low",
      consultationFee: 500,
      rating: 4.5,
      qualifications: "MBBS, MD (Internal Medicine)",
      languages: "English, Hindi",
    },
    {
      id: "5",
      name: "Dr. Ananya Reddy",
      specialist: "Dermatologist",
      system: "Dermatology",
      hospital: "Apollo Hospitals",
      experience: "14 years",
      availability: "busy",
      busyRate: "High",
      consultationFee: 800,
      rating: 4.7,
      qualifications: "MBBS, MD (Dermatology)",
      languages: "English, Hindi, Telugu",
    },
    {
      id: "6",
      name: "Dr. Suresh Desai",
      specialist: "Orthodontist",
      system: "Orthodontics",
      hospital: "Apollo Hospitals",
      experience: "18 years",
      availability: "moderate",
      busyRate: "Medium",
      consultationFee: 600,
      rating: 4.8,
      qualifications: "BDS, MDS (Orthodontics)",
      languages: "English, Hindi, Marathi",
    },
    {
      id: "7",
      name: "Dr. Meena Gupta",
      specialist: "Pediatrician",
      system: "Pediatric Care",
      hospital: "Fortis Healthcare",
      experience: "8 years",
      availability: "available",
      busyRate: "Low",
      consultationFee: 550,
      rating: 4.4,
      qualifications: "MBBS, MD (Pediatrics)",
      languages: "English, Hindi",
    },
    {
      id: "8",
      name: "Dr. Suresh Reddy",
      specialist: "Oncologist",
      system: "Cancer Care",
      hospital: "Fortis Healthcare",
      experience: "20 years",
      availability: "busy",
      busyRate: "High",
      consultationFee: 1200,
      rating: 4.9,
      qualifications: "MBBS, MD, DM (Oncology)",
      languages: "English, Hindi, Telugu",
    },
    {
      id: "9",
      name: "Dr. Vikram Desai",
      specialist: "Gastroenterologist",
      system: "Digestive",
      hospital: "Max Healthcare",
      experience: "14 years",
      availability: "available",
      busyRate: "Low",
      consultationFee: 700,
      rating: 4.6,
      qualifications: "MBBS, MD, DM (Gastroenterology)",
      languages: "English, Hindi, Gujarati",
    },
    {
      id: "10",
      name: "Dr. Sanjay Mehta",
      specialist: "Endocrinologist",
      system: "Endocrine",
      hospital: "Manipal Hospitals",
      experience: "16 years",
      availability: "busy",
      busyRate: "High",
      consultationFee: 800,
      rating: 4.7,
      qualifications: "MBBS, MD, DM (Endocrinology)",
      languages: "English, Hindi",
    },
  ]

  // Generate calendar dates for current month
  const generateCalendarDates = () => {
    const today = new Date()
    const currentMonth = today.getMonth()
    const currentYear = today.getFullYear()
    const firstDay = new Date(currentYear, currentMonth, 1)
    const lastDay = new Date(currentYear, currentMonth + 1, 0)
    const startDate = new Date(firstDay)
    startDate.setDate(startDate.getDate() - firstDay.getDay())

    const dates = []
    for (let i = 0; i < 42; i++) {
      const date = new Date(startDate)
      date.setDate(startDate.getDate() + i)
      dates.push(date)
    }
    return dates
  }

  const calendarDates = generateCalendarDates()
  const today = new Date()

  // Time slots with busy rates based on doctor's availability
  const getTimeSlotsWithBusyRate = () => {
    const baseSlots = [
      "09:00 AM",
      "09:30 AM",
      "10:00 AM",
      "10:30 AM",
      "11:00 AM",
      "11:30 AM",
      "02:00 PM",
      "02:30 PM",
      "03:00 PM",
      "03:30 PM",
      "04:00 PM",
      "04:30 PM",
    ]

    // Generate busy rates based on doctor's overall availability and time of day
    return baseSlots.map((time, index) => {
      let busyRate = "available" // default

      // Morning slots (9-12) tend to be busier
      if (index < 6) {
        if (doctor.availability === "busy") {
          busyRate = Math.random() > 0.3 ? "busy" : "moderate"
        } else if (doctor.availability === "moderate") {
          busyRate = Math.random() > 0.5 ? "moderate" : "available"
        } else {
          busyRate = Math.random() > 0.7 ? "moderate" : "available"
        }
      }
      // Afternoon slots (2-5) are generally less busy
      else {
        if (doctor.availability === "busy") {
          busyRate = Math.random() > 0.4 ? "moderate" : "busy"
        } else if (doctor.availability === "moderate") {
          busyRate = Math.random() > 0.6 ? "available" : "moderate"
        } else {
          busyRate = "available"
        }
      }

      return {
        time,
        busyRate,
        available: busyRate !== "busy" || Math.random() > 0.8, // Some busy slots might still be available
      }
    })
  }

  const [timeSlots, setTimeSlots] = useState([])

  useEffect(() => {
    // Find the doctor based on doctorId, or use default if not found
    const foundDoctor = allDoctors.find((d) => d.id === doctorId)
    if (foundDoctor) {
      setDoctor(foundDoctor)
    }
  }, [doctorId])

  useEffect(() => {
    // Generate time slots when doctor or selected date changes
    if (selectedDate) {
      setTimeSlots(getTimeSlotsWithBusyRate())
    }
  }, [doctor, selectedDate])

  const handleBack = () => {
    router.back()
  }

  const handleDateSelect = (date) => {
    if (date >= today) {
      setSelectedDate(date)
      setSelectedTime(null) // Reset time selection when date changes
    }
  }

  const handleTimeSelect = (timeSlot) => {
    if (timeSlot.available) {
      setSelectedTime(timeSlot.time)
    }
  }

  const handleBookAppointment = () => {
    if (!selectedDate || !selectedTime) {
      alert("Please select both date and time for your appointment")
      return
    }

    // Navigate to confirmation page with appointment data including availability
    const params = new URLSearchParams({
      doctorName: doctor.name,
      date: selectedDate.toDateString(),
      time: selectedTime,
      fee: doctor.consultationFee.toString(),
      availability: doctor.availability,
    })

    router.push(`/confirmation?${params.toString()}`)
  }

  const formatDate = (date) => {
    return date.getDate()
  }

  const isToday = (date) => {
    return date.toDateString() === today.toDateString()
  }

  const isPastDate = (date) => {
    return date < today
  }

  const isCurrentMonth = (date) => {
    return date.getMonth() === today.getMonth()
  }

  const getTimeSlotColor = (timeSlot) => {
    if (!timeSlot.available) {
      return "bg-gray-200 text-gray-500 cursor-not-allowed border border-gray-300"
    }

    if (selectedTime === timeSlot.time) {
      return "bg-gradient-to-r from-teal-500 to-teal-600 text-white shadow-lg border-2 border-teal-400"
    }

    switch (timeSlot.busyRate) {
      case "available":
        return "bg-gradient-to-r from-green-50 to-green-100 text-green-800 hover:from-green-100 hover:to-green-200 border-2 border-green-200 hover:border-green-300 shadow-md hover:shadow-lg"
      case "moderate":
        return "bg-gradient-to-r from-yellow-50 to-yellow-100 text-yellow-800 hover:from-yellow-100 hover:to-yellow-200 border-2 border-yellow-200 hover:border-yellow-300 shadow-md hover:shadow-lg"
      case "busy":
        return timeSlot.available
          ? "bg-gradient-to-r from-orange-50 to-orange-100 text-orange-800 hover:from-orange-100 hover:to-orange-200 border-2 border-orange-200 hover:border-orange-300 shadow-md hover:shadow-lg"
          : "bg-gray-200 text-gray-500 cursor-not-allowed border-2 border-gray-300"
      default:
        return "bg-gradient-to-r from-gray-50 to-gray-100 text-gray-700 hover:from-teal-50 hover:to-teal-100 border-2 border-gray-200 hover:border-teal-300 shadow-md hover:shadow-lg"
    }
  }

  const getBusyRateLabel = (busyRate) => {
    switch (busyRate) {
      case "available":
        return "Available"
      case "moderate":
        return "Moderate"
      case "busy":
        return "Busy"
      default:
        return ""
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-50 via-blue-50 to-teal-50 relative overflow-hidden">
      {/* Enhanced Animated Bubble Background */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating Medical Icons */}
        <div className="absolute top-20 left-10 animate-float opacity-30">
          <Heart className="w-6 h-6 text-red-300" />
        </div>
        <div className="absolute top-40 right-16 animate-float-delayed opacity-25">
          <Stethoscope className="w-8 h-8 text-teal-300" />
        </div>
        <div className="absolute bottom-32 left-20 animate-float opacity-30">
          <Plus className="w-5 h-5 text-blue-300" />
        </div>
        <div className="absolute bottom-20 right-10 animate-float-delayed opacity-25">
          <Activity className="w-7 h-7 text-green-300" />
        </div>
        <div className="absolute top-1/2 left-1/2 animate-pulse-slow opacity-20">
          <svg
            viewBox="0 0 24 24"
            fill="none"
            stroke="currentColor"
            strokeWidth="2"
            className="w-10 h-10 text-purple-300"
          >
            <path d="M8 21s-4-3-4-9 4-9 4-9 4 3 4 9-4 9-4 9z" />
            <path d="m16 21s4-3 4-9-4-9-4-9-4 3-4 9 4 9 4 9z" />
          </svg>
        </div>

        {/* Animated Gradient Bubble Orbs */}
        <div className="absolute top-1/4 left-1/4 w-32 h-32 bg-gradient-to-r from-teal-200 to-blue-200 rounded-full opacity-30 animate-pulse-slow blur-xl"></div>
        <div className="absolute bottom-1/4 right-1/4 w-24 h-24 bg-gradient-to-r from-blue-200 to-green-200 rounded-full opacity-25 animate-pulse-slower blur-xl"></div>
        <div className="absolute top-1/2 right-1/3 w-28 h-28 bg-gradient-to-r from-purple-200 to-pink-200 rounded-full opacity-20 animate-pulse-slow blur-xl"></div>
        <div className="absolute top-3/4 left-1/3 w-20 h-20 bg-gradient-to-r from-yellow-200 to-orange-200 rounded-full opacity-25 animate-pulse-slower blur-xl"></div>
        <div className="absolute top-1/6 right-1/6 w-16 h-16 bg-gradient-to-r from-indigo-200 to-cyan-200 rounded-full opacity-30 animate-pulse-slow blur-xl"></div>
      </div>

      {/* Enhanced Medical Header */}
      <div className="bg-gradient-to-r from-teal-600 via-teal-700 to-emerald-700 px-4 py-4 flex items-center justify-between shadow-xl relative overflow-hidden">
        {/* ECG Line Animation */}
        <div className="absolute top-0 left-0 w-full h-full opacity-10">
          <svg
            width="100%"
            height="100%"
            viewBox="0 0 1000 60"
            preserveAspectRatio="none"
            className="absolute top-1/2 transform -translate-y-1/2"
          >
            <path
              d="M0,30 L100,30 L120,15 L140,45 L160,10 L180,50 L200,25 L220,30 L300,30 L320,20 L340,40 L360,15 L380,45 L400,30 L500,30 L520,18 L540,42 L560,12 L580,48 L600,28 L620,30 L700,30 L720,22 L740,38 L760,14 L780,46 L800,30 L1000,30"
              fill="none"
              stroke="white"
              strokeWidth="1.5"
              strokeLinecap="round"
              strokeLinejoin="round"
              className="animate-heartbeat"
            />
          </svg>
        </div>

        <Button
          variant="ghost"
          size="icon"
          onClick={handleBack}
          className="text-white hover:bg-white/20 relative z-10 transition-all duration-300 hover:scale-110 group"
        >
          <ArrowLeft className="w-8 h-8 group-hover:-translate-x-1 transition-transform duration-300" />
        </Button>
        <h1 className="text-white text-3xl font-bold relative z-10 animate-pulse">
          <span className="bg-gradient-to-r from-white to-blue-100 bg-clip-text text-transparent">BETSHOW</span>
        </h1>
        <Button
          variant="ghost"
          size="icon"
          className="text-white hover:bg-white/20 relative z-10 transition-all duration-300 hover:scale-110 group"
        >
          <MoreHorizontal className="w-6 h-6 group-hover:rotate-90 transition-transform duration-300" />
        </Button>
      </div>

      {/* Enhanced Calendar Section */}
      <div className="px-4 py-6 relative z-10">
        <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-xl border-2 border-white/50 p-6 mb-6">
          <div className="flex items-center gap-3 mb-6">
            <div className="w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
            <h2 className="text-xl font-bold text-gray-800">Select Appointment Date</h2>
            <Calendar className="w-5 h-5 text-blue-600" />
          </div>

          <div className="grid grid-cols-7 gap-2 mb-6">
            {["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"].map((day) => (
              <div
                key={day}
                className="text-center text-sm font-bold text-teal-700 py-3 bg-gradient-to-r from-teal-50 to-blue-50 rounded-lg"
              >
                {day}
              </div>
            ))}
            {calendarDates.map((date, index) => (
              <button
                key={index}
                onClick={() => handleDateSelect(date)}
                disabled={isPastDate(date)}
                className={`
                  h-12 w-full rounded-xl text-sm font-medium transition-all duration-300 border-2
                  ${
                    isPastDate(date)
                      ? "text-gray-300 cursor-not-allowed bg-gray-50 border-gray-200"
                      : isCurrentMonth(date)
                        ? "text-gray-800 hover:bg-gradient-to-r hover:from-teal-50 hover:to-blue-50 bg-white border-gray-200 hover:border-teal-300 hover:scale-105 shadow-sm hover:shadow-md"
                        : "text-gray-400 hover:bg-gray-50 bg-white border-gray-100"
                  }
                  ${isToday(date) ? "bg-gradient-to-r from-blue-100 to-teal-100 text-teal-800 font-bold border-teal-300 shadow-md" : ""}
                  ${selectedDate && selectedDate.toDateString() === date.toDateString() ? "bg-gradient-to-r from-teal-500 to-teal-600 text-white border-teal-400 shadow-lg scale-105" : ""}
                `}
              >
                {formatDate(date)}
              </button>
            ))}
          </div>
        </div>

        {/* Enhanced Time Slots */}
        {selectedDate && (
          <div className="bg-white/90 backdrop-blur-sm rounded-2xl shadow-xl border-2 border-white/50 p-6 mb-6">
            <div className="flex items-center gap-3 mb-6">
              <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse"></div>
              <h3 className="text-lg font-bold text-gray-800">Available Time Slots</h3>
              <Clock className="w-5 h-5 text-green-600" />
            </div>

            {/* Enhanced Legend */}
            <div className="flex flex-wrap gap-4 mb-6 p-4 bg-gradient-to-r from-gray-50 to-blue-50 rounded-xl border border-gray-200">
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-gradient-to-r from-green-100 to-green-200 border-2 border-green-300 rounded"></div>
                <span className="text-sm font-medium text-gray-700">Available</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-gradient-to-r from-yellow-100 to-yellow-200 border-2 border-yellow-300 rounded"></div>
                <span className="text-sm font-medium text-gray-700">Moderate</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-gradient-to-r from-orange-100 to-orange-200 border-2 border-orange-300 rounded"></div>
                <span className="text-sm font-medium text-gray-700">Busy</span>
              </div>
              <div className="flex items-center gap-2">
                <div className="w-4 h-4 bg-gray-200 border-2 border-gray-300 rounded"></div>
                <span className="text-sm font-medium text-gray-700">Unavailable</span>
              </div>
            </div>

            <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
              {timeSlots.map((timeSlot, index) => (
                <button
                  key={index}
                  onClick={() => handleTimeSelect(timeSlot)}
                  disabled={!timeSlot.available}
                  className={`
                    py-4 px-4 rounded-xl text-sm font-bold transition-all duration-300 relative transform hover:scale-105
                    ${getTimeSlotColor(timeSlot)}
                  `}
                >
                  <div className="text-center">
                    <div className="text-base font-bold">{timeSlot.time}</div>
                    {timeSlot.available && (
                      <div className="text-xs opacity-80 mt-1 font-medium">{getBusyRateLabel(timeSlot.busyRate)}</div>
                    )}
                  </div>
                  {selectedTime === timeSlot.time && (
                    <div className="absolute -top-1 -right-1">
                      <div className="w-3 h-3 bg-white rounded-full animate-pulse"></div>
                    </div>
                  )}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>

      {/* Enhanced Doctor Details Section */}
      <div className="px-4 py-6 pb-32 relative z-10">
        <div className="bg-white/95 backdrop-blur-sm rounded-2xl shadow-xl border-2 border-white/50 overflow-hidden">
          {/* Medical Header */}
          <div className="bg-gradient-to-r from-teal-500/10 to-blue-500/10 px-6 py-4 border-b-2 border-teal-100">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-3">
                <div className="w-3 h-3 bg-teal-500 rounded-full animate-pulse"></div>
                <span className="text-sm font-bold text-teal-700">MEDICAL PROFESSIONAL</span>
                <Stethoscope className="w-4 h-4 text-teal-600" />
              </div>
              <div className="flex items-center gap-2">
                <div className="w-2 h-2 bg-green-500 rounded-full animate-pulse"></div>
                <span className="text-xs font-medium text-green-600">AVAILABLE</span>
              </div>
            </div>
          </div>

          <div className="p-6">
            <div className="flex items-start space-x-6 mb-6">
              {/* Enhanced Doctor Avatar */}
              <div className="relative">
                <div className="w-24 h-24 bg-gradient-to-br from-teal-100 to-blue-100 rounded-full flex items-center justify-center border-4 border-white shadow-lg">
                  <User className="w-12 h-12 text-teal-600" />
                </div>
                <div className="absolute -bottom-1 -right-1 w-6 h-6 bg-green-500 rounded-full border-2 border-white flex items-center justify-center">
                  <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                </div>
              </div>

              {/* Doctor Info */}
              <div className="flex-1">
                <h3 className="text-2xl font-bold text-teal-800 mb-2">{doctor.name}</h3>
                <div className="flex items-center gap-2 mb-2">
                  <Stethoscope className="w-4 h-4 text-teal-600" />
                  <p className="text-lg font-semibold text-teal-700">{doctor.specialist}</p>
                </div>
                <div className="inline-flex items-center gap-2 bg-gradient-to-r from-blue-50 to-teal-50 px-3 py-1 rounded-full border border-blue-200">
                  <Award className="w-3 h-3 text-blue-600" />
                  <p className="text-sm font-medium text-blue-700">{doctor.qualifications}</p>
                </div>
              </div>
            </div>

            {/* Enhanced Medical Information Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
              <div className="bg-gradient-to-r from-blue-50 to-indigo-50 p-4 rounded-xl border border-blue-200">
                <div className="flex items-center space-x-3">
                  <MapPin className="w-5 h-5 text-blue-600" />
                  <div>
                    <span className="text-sm font-bold text-gray-700">Hospital:</span>
                    <p className="text-blue-700 font-semibold">{doctor.hospital}</p>
                  </div>
                </div>
              </div>

              <div className="bg-gradient-to-r from-green-50 to-emerald-50 p-4 rounded-xl border border-green-200">
                <div className="flex items-center space-x-3">
                  <Clock className="w-5 h-5 text-green-600" />
                  <div>
                    <span className="text-sm font-bold text-gray-700">Experience:</span>
                    <p className="text-green-700 font-semibold">{doctor.experience}</p>
                  </div>
                </div>
              </div>

              <div className="bg-gradient-to-r from-yellow-50 to-orange-50 p-4 rounded-xl border border-yellow-200">
                <div className="flex items-center space-x-3">
                  <DollarSign className="w-5 h-5 text-yellow-600" />
                  <div>
                    <span className="text-sm font-bold text-gray-700">Consultation Fee:</span>
                    <p className="text-yellow-700 font-bold text-lg">{doctor.consultationFee}</p>
                  </div>
                </div>
              </div>

              <div className="bg-gradient-to-r from-purple-50 to-pink-50 p-4 rounded-xl border border-purple-200">
                <div className="flex items-center space-x-3">
                  <Star className="w-5 h-5 text-purple-600 fill-purple-600" />
                  <div>
                    <span className="text-sm font-bold text-gray-700">Rating:</span>
                    <p className="text-purple-700 font-semibold">{doctor.rating}/5.0</p>
                  </div>
                </div>
              </div>
            </div>

            {/* Additional Info */}
            <div className="space-y-4">
              <div className="bg-gradient-to-r from-teal-50 to-cyan-50 p-4 rounded-xl border border-teal-200">
                <div className="flex items-center space-x-3">
                  <svg className="w-5 h-5 text-teal-600" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm-2 15l-5-5 1.41-1.41L10 14.17l7.59-7.59L19 8l-9 9z" />
                  </svg>
                  <div>
                    <span className="text-sm font-bold text-gray-700">Languages:</span>
                    <p className="text-teal-700 font-semibold">{doctor.languages}</p>
                  </div>
                </div>
              </div>

              <div className="bg-gradient-to-r from-gray-50 to-slate-50 p-4 rounded-xl border border-gray-200">
                <div className="flex items-center space-x-3">
                  <Activity className="w-5 h-5 text-gray-600" />
                  <div>
                    <span className="text-sm font-bold text-gray-700">Availability Status:</span>
                    <span
                      className={`ml-2 px-3 py-1 rounded-full text-xs font-bold ${
                        doctor.availability === "available"
                          ? "bg-green-100 text-green-800 border border-green-300"
                          : doctor.availability === "moderate"
                            ? "bg-yellow-100 text-yellow-800 border border-yellow-300"
                            : "bg-red-100 text-red-800 border border-red-300"
                      }`}
                    >
                      {doctor.busyRate} Demand
                    </span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Enhanced Book Appointment Button */}
      <div className="fixed bottom-0 left-0 right-0 bg-gradient-to-t from-white via-white/95 to-transparent backdrop-blur-sm border-t-2 border-white/50 p-4 z-20">
        <Button
          onClick={handleBookAppointment}
          className="w-full bg-gradient-to-r from-teal-500 to-teal-600 hover:from-teal-600 hover:to-teal-700 text-white text-lg font-bold py-4 rounded-xl transition-all duration-300 transform hover:scale-[1.02] hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none group relative overflow-hidden shadow-lg"
          disabled={!selectedDate || !selectedTime}
        >
          <span className="relative z-10 flex items-center justify-center gap-3">
            <Calendar className="w-5 h-5 group-hover:animate-bounce" />
            BOOK APPOINTMENT
            <Stethoscope className="w-5 h-5 group-hover:animate-pulse" />
          </span>
          <div className="absolute inset-0 bg-gradient-to-r from-white/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
          {!selectedDate || !selectedTime ? <div className="absolute inset-0 bg-gray-500/20 rounded-xl"></div> : null}
        </Button>
      </div>

      {/* Enhanced Medical Chat Button */}
      <div className="fixed bottom-20 right-6 z-30">
        <Button className="bg-gradient-to-r from-teal-500 to-teal-600 hover:from-teal-600 hover:to-teal-700 text-white rounded-full w-16 h-16 p-0 shadow-xl hover:shadow-2xl transition-all duration-300 transform hover:scale-110 group relative overflow-hidden">
          <div className="absolute inset-0 bg-gradient-to-r from-white/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300 rounded-full"></div>
          <div className="absolute inset-0 animate-ping bg-teal-400 rounded-full opacity-20"></div>
          <Heart className="w-7 h-7 relative z-10 group-hover:animate-pulse text-red-100" />
        </Button>
      </div>

      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-15px) rotate(5deg); }
        }
        
        @keyframes float-delayed {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(-5deg); }
        }
        
        @keyframes pulse-slow {
          0%, 100% { opacity: 0.3; transform: scale(1); }
          50% { opacity: 0.6; transform: scale(1.05); }
        }
        
        @keyframes pulse-slower {
          0%, 100% { opacity: 0.25; transform: scale(1); }
          50% { opacity: 0.5; transform: scale(1.1); }
        }
        
        @keyframes heartbeat {
          0%, 100% { stroke-dasharray: 0, 2000; }
          50% { stroke-dasharray: 2000, 0; }
        }
        
        .animate-float {
          animation: float 4s ease-in-out infinite;
        }
        
        .animate-float-delayed {
          animation: float-delayed 5s ease-in-out infinite;
        }
        
        .animate-pulse-slow {
          animation: pulse-slow 4s ease-in-out infinite;
        }
        
        .animate-pulse-slower {
          animation: pulse-slower 6s ease-in-out infinite;
        }
        
        .animate-heartbeat {
          animation: heartbeat 4s ease-in-out infinite;
        }
      `}</style>
    </div>
  )
}
