"use client"

import { useState, useEffect } from "react"
import { useNavigate } from "react-router-dom"
import { Button } from "./ui/button"
import { Input } from "./ui/input"
import { Label } from "./ui/label"
import { Heart, Activity, Stethoscope, Plus } from "lucide-react"

export default function OtpForm() {
  const navigate = useNavigate()
  const [otp, setOtp] = useState("")
  const [isLoading, setIsLoading] = useState(false)

  useEffect(() => {
    window.scrollTo(0, 0)
  }, [])

  const handleInputChange = (value) => {
    // Only allow numbers and limit to 6 digits
    const numbersOnly = value.replace(/\D/g, "").slice(0, 6)
    setOtp(numbersOnly)
  }

  const handleVerifyOTP = async () => {
    if (!otp.trim()) {
      alert("Please enter the OTP")
      return
    }

    if (otp.length !== 6) {
      alert("OTP must be 6 digits")
      return
    }

    setIsLoading(true)

    // Simulate API call
    await new Promise((resolve) => setTimeout(resolve, 2000))

    // For demo purposes, accept any 6-digit OTP
    setIsLoading(false)
    navigate("/home")
  }

  const handlePrevious = () => {
    navigate("/abha")
  }

  const handleResendOTP = () => {
    alert("OTP has been resent to your registered mobile number")
  }

  return (
    <div className="min-h-screen bg-gray-50 font-be-vietnam">
      {/* Enhanced Animated Bubble Background */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {/* Floating Medical Icons */}
        <div className="absolute top-20 left-10 animate-float opacity-30">
          <Heart className="w-6 h-6 text-red-300" />
        </div>
        <div className="absolute top-40 right-16 animate-float-delayed opacity-25">
          <Stethoscope className="w-8 h-8 text-teal-300" />
        </div>
        <div className="absolute bottom-32 left-20 animate-float opacity-30">
          <Plus className="w-5 h-5 text-blue-300" />
        </div>
        <div className="absolute bottom-20 right-10 animate-float-delayed opacity-25">
          <Activity className="w-7 h-7 text-green-300" />
        </div>

        {/* Animated Gradient Bubble Orbs */}
        <div className="absolute top-1/4 left-1/4 w-32 h-32 bg-gradient-to-r from-teal-200 to-blue-200 rounded-full opacity-30 animate-pulse-slow blur-xl"></div>
        <div className="absolute bottom-1/4 right-1/4 w-24 h-24 bg-gradient-to-r from-blue-200 to-green-200 rounded-full opacity-25 animate-pulse-slower blur-xl"></div>
        <div className="absolute top-1/2 right-1/3 w-28 h-28 bg-gradient-to-r from-purple-200 to-pink-200 rounded-full opacity-20 animate-pulse-slow blur-xl"></div>
        <div className="absolute top-3/4 left-1/3 w-20 h-20 bg-gradient-to-r from-yellow-200 to-orange-200 rounded-full opacity-25 animate-pulse-slower blur-xl"></div>
        <div className="absolute top-1/6 right-1/6 w-16 h-16 bg-gradient-to-r from-indigo-200 to-cyan-200 rounded-full opacity-30 animate-pulse-slow blur-xl"></div>
      </div>

      <div className="relative w-full h-32 bg-gradient-to-br from-teal-600 via-teal-700 to-emerald-700 overflow-hidden">
        <div className="absolute inset-0 opacity-10">
          <svg width="100%" height="100%" viewBox="0 0 60 60" className="w-full h-full">
            <defs>
              <pattern id="hexPattern" x="0" y="0" width="30" height="26" patternUnits="userSpaceOnUse">
                <path d="M15,2 L25,8 L25,20 L15,26 L5,20 L5,8 Z" fill="none" stroke="white" strokeWidth="0.5" />
              </pattern>
            </defs>
            <rect width="100%" height="100%" fill="url(#hexPattern)" />
          </svg>
        </div>

        <div className="absolute top-1/2 left-0 w-full transform -translate-y-1/2 opacity-60">
          <svg width="100%" height="60" viewBox="0 0 1000 60" preserveAspectRatio="none">
            <path
              d="M0,30 L100,30 L120,15 L140,45 L160,10 L180,50 L200,25 L220,30 L300,30 L320,20 L340,40 L360,15 L380,45 L400,30 L500,30 L520,18 L540,42 L560,12 L580,48 L600,28 L620,30 L700,30 L720,22 L740,38 L760,14 L780,46 L800,30 L1000,30"
              fill="none"
              stroke="white"
              strokeWidth="2"
              strokeLinecap="round"
              strokeLinejoin="round"
            >
              <animate attributeName="stroke-dasharray" values="0,2000;2000,0" dur="4s" repeatCount="indefinite" />
            </path>
          </svg>
        </div>

        <div className="absolute inset-0 flex items-center justify-between px-12">
          <div className="opacity-40">
            <Heart className="w-8 h-8 text-white">
              <animate attributeName="opacity" values="0.4;0.8;0.4" dur="3s" repeatCount="indefinite" />
            </Heart>
          </div>
          <div className="opacity-50">
            <Activity className="w-10 h-10 text-white" />
          </div>
        </div>

        <div className="absolute right-8 top-0 h-full opacity-30">
          <svg width="40" height="128" viewBox="0 0 40 128">
            <path
              d="M10,0 Q25,16 30,32 Q25,48 10,64 Q25,80 30,96 Q25,112 10,128"
              fill="none"
              stroke="white"
              strokeWidth="1.5"
              strokeDasharray="3,3"
            >
              <animateTransform
                attributeName="transform"
                type="translate"
                values="0,0;0,-10;0,0"
                dur="6s"
                repeatCount="indefinite"
              />
            </path>
            <path
              d="M30,0 Q15,16 10,32 Q15,48 30,64 Q15,80 10,96 Q15,112 30,128"
              fill="none"
              stroke="white"
              strokeWidth="1.5"
              strokeDasharray="3,3"
            >
              <animateTransform
                attributeName="transform"
                type="translate"
                values="0,0;0,10;0,0"
                dur="6s"
                repeatCount="indefinite"
              />
            </path>
          </svg>
        </div>

        <div className="absolute left-8 top-1/2 transform -translate-y-1/2 opacity-40">
          <svg width="32" height="32" viewBox="0 0 32 32">
            <path d="M16,6 L16,26 M6,16 L26,16" stroke="white" strokeWidth="3" strokeLinecap="round" />
          </svg>
        </div>

        <div className="absolute inset-0 bg-gradient-to-t from-black/10 to-transparent"></div>
      </div>

      <div className="px-6 py-8 space-y-8">
        <div className="text-center space-y-4">
          <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-teal-600 to-blue-600 animate-fade-in font-be-vietnam">
            Verify OTP
          </h1>
          <p className="text-lg text-gray-600 animate-fade-in-delayed font-be-vietnam">
            Please enter the 6-digit OTP sent to your registered mobile number
          </p>
        </div>

        <div className="space-y-8">
          <div className="space-y-3 animate-slide-up">
            <Label className="text-xl font-semibold text-gray-800 flex items-center gap-2 font-be-vietnam">
              <div className="w-2 h-2 bg-teal-500 rounded-full animate-pulse"></div>
              Enter OTP :
            </Label>
            <div className="relative group">
              <Input
                type="text"
                inputMode="numeric"
                pattern="[0-9]*"
                placeholder="Enter 6-digit OTP"
                value={otp}
                onChange={(e) => handleInputChange(e.target.value)}
                className="h-14 bg-white/80 backdrop-blur-sm border-2 border-gray-200 rounded-xl text-gray-700 placeholder:text-gray-500 text-lg px-4 transition-all duration-300 focus:border-teal-400 focus:shadow-lg focus:shadow-teal-100 hover:border-teal-300 font-be-vietnam text-center tracking-widest"
                maxLength="6"
                required
              />
              <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-teal-400/10 to-blue-400/10 opacity-0 group-hover:opacity-100 transition-opacity duration-300 pointer-events-none"></div>
            </div>
            <div className="text-center">
              <button
                onClick={handleResendOTP}
                className="text-teal-600 hover:text-teal-700 font-medium text-sm underline font-be-vietnam"
              >
                Resend OTP
              </button>
            </div>
          </div>
        </div>

        <div className="pt-8 flex justify-between">
          <Button
            onClick={handlePrevious}
            className="bg-teal-500 hover:bg-teal-600 text-white font-semibold text-lg px-8 py-3 rounded-lg border-0 font-be-vietnam"
          >
            Previous
          </Button>
          <Button
            onClick={handleVerifyOTP}
            disabled={isLoading}
            className="relative bg-teal-300 hover:bg-teal-400 text-black font-semibold text-lg px-8 py-3 rounded-lg border-0 disabled:opacity-50 disabled:cursor-not-allowed group overflow-hidden font-be-vietnam"
          >
            {isLoading ? (
              <div className="flex items-center gap-2">
                <div className="w-5 h-5 border-2 border-gray-600/30 border-t-gray-600 rounded-full animate-spin"></div>
                Verifying...
              </div>
            ) : (
              <>
                <span className="relative z-10">Verify</span>
                <div className="absolute inset-0 bg-gradient-to-r from-white/20 to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
              </>
            )}
          </Button>
        </div>
      </div>

      {/* Enhanced Custom CSS for bubble animations */}
      <style jsx>{`
        @keyframes float {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-10px) rotate(5deg); }
        }
        
        @keyframes float-delayed {
          0%, 100% { transform: translateY(0px) rotate(0deg); }
          50% { transform: translateY(-15px) rotate(-5deg); }
        }
        
        @keyframes pulse-slow {
          0%, 100% { opacity: 0.3; transform: scale(1); }
          50% { opacity: 0.6; transform: scale(1.05); }
        }
        
        @keyframes pulse-slower {
          0%, 100% { opacity: 0.25; transform: scale(1); }
          50% { opacity: 0.5; transform: scale(1.1); }
        }
        
        @keyframes fade-in {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        
        @keyframes fade-in-delayed {
          from { opacity: 0; transform: translateY(20px); }
          to { opacity: 1; transform: translateY(0); }
        }
        
        @keyframes slide-up {
          from { opacity: 0; transform: translateY(30px); }
          to { opacity: 1; transform: translateY(0); }
        }
        
        .animate-float {
          animation: float 3s ease-in-out infinite;
        }
        
        .animate-float-delayed {
          animation: float-delayed 4s ease-in-out infinite;
        }
        
        .animate-pulse-slow {
          animation: pulse-slow 3s ease-in-out infinite;
        }
        
        .animate-pulse-slower {
          animation: pulse-slower 4s ease-in-out infinite;
        }
        
        .animate-fade-in {
          animation: fade-in 0.8s ease-out;
        }
        
        .animate-fade-in-delayed {
          animation: fade-in-delayed 1s ease-out 0.2s both;
        }
        
        .animate-slide-up {
          animation: slide-up 0.6s ease-out 0.3s both;
        }
      `}</style>
    </div>
  )
}
