"use client"

import { useState, useEffect } from "react"
import { useLocation } from "react-router-dom"

const ConfirmationPage = () => {
  const location = useLocation()
  const [doctorName, setDoctorName] = useState("")
  const [date, setDate] = useState("")
  const [time, setTime] = useState("")
  const [fee, setFee] = useState("")
  const [availability, setAvailability] = useState("")

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search)
    setDoctorName(searchParams.get("doctorName") || "")
    setDate(searchParams.get("date") || "")
    setTime(searchParams.get("time") || "")
    setFee(searchParams.get("fee") || "")
    setAvailability(searchParams.get("availability") || "")

    // Save appointment data to localStorage
    if (doctorName && date && time && fee) {
      const appointmentData = {
        doctorName,
        date,
        time,
        fee,
        availability: availability || "Available",
        bookedAt: new Date().toISOString(),
        status: "Confirmed",
      }
      localStorage.setItem("appointmentData", JSON.stringify(appointmentData))
    }
  }, [location.search, doctorName, date, time, fee, availability])

  return (
    <div>
      <h1>Appointment Confirmation</h1>
      {doctorName && (
        <p>
          You have an appointment with Dr. {doctorName} on {date} at {time}.
        </p>
      )}
      {fee && <p>Consultation Fee: ${fee}</p>}
    </div>
  )
}

export default ConfirmationPage
